import React, { Component } from 'react';
import ProductList from '../../components/ProductList'

class Product extends Component {
	constructor(){
		super();

		this.state = {
			productlist: [],
			productName: "",
			isFatching: false
		}


	}

	// componentDidMount(){
	// 	fetch('http://makeup-api.herokuapp.com/api/v1/products.json?brand=maybelline')
	// 	.then(response => response.json())
	// 	.then(json => this.setState({ productlist: json }))
	// }

	OnProductinputchange = e => {
		this.setState({ productName: e.target.value, isFatching: true});

		fetch(`http://makeup-api.herokuapp.com/api/v1/products.json?brand=${e.target.value}`)
		.then(response => response.json())
		.then(json => this.setState({ productlist: json, isFatching: false }))
	} 

	render(){ 
		const { productlist, productName, isFatching } = this.state;
		return(
			<div>
				<div>
					<input 
						value={productName} 
						type="text" 
						onChange={this.OnProductinputchange} />
				</div>
				{
					productlist.length === 0 && productName.trim() == ""
					&&
					<p>Please enter product name into input field</p>
				}
				<ProductList list={this.state.productlist} />
			</div>
		)
	}
}

export default Product;
