import React, { Component } from 'react';
import ProductList from '../../components/ProductList'

class Product extends Component {
	constructor(){
		super();

		this.state = {
			productlist: []
		}

		this.OnProductinputchange = this.OnProductinputchange.bind(this);
	}

	componentDidMount(){
		fetch('http://makeup-api.herokuapp.com/api/v1/products.json?brand=maybelline')
		.then(response => response.json())
		.then(json => this.setState({ productlist: json }))
	}

	OnProductinputchange = e => {
		console.log(e)
		console.log(e.target.value)
	} 

	render(){ 
		return(
			<div>
				The length of List array: { this.state.productlist.length }
				<div>
					<input type="text" onChange={this.OnProductinputchange} />
				</div>
				<ProductList list={this.state.productlist} />
			</div>
		)
	}
}

export default Product;
